    /// @copydoc binary(const typename binary_t::container_type&)

    JSON_HEDLEY_WARN_UNUSED_RESULT

    static basic_json binary(typename binary_t::container_type&& init)

    {

        auto res = basic_json();

        res.m_type = value_t::binary;

        res.m_value = std::move(init);

        return res;

