    @copydoc iterator_wrapper(reference)

    */

    JSON_HEDLEY_DEPRECATED_FOR(3.1.0, items())

    static iteration_proxy<const_iterator> iterator_wrapper(const_reference ref) noexcept

    {

        return ref.items();

