    const char* type_name() const noexcept

    {

        {

            switch (m_type)

            {

                case value_t::null:

                    return "null";

                case value_t::object:

                    return "object";

                case value_t::array:

                    return "array";

                case value_t::string:

                    return "string";

                case value_t::boolean:

                    return "boolean";

                case value_t::binary:

                    return "binary";

                case value_t::discarded:

                    return "discarded";

