            { 0xB94470938FA89BCF,  -316,  -76 },

            { 0x8A08F0F8BF0F156B,  -289,  -68 },

            { 0xCDB02555653131B6,  -263,  -60 },

            { 0x993FE2C6D07B7FAC,  -236,  -52 },

            { 0xE45C10C42A2B3B06,  -210,  -44 },

