            { 0x9C40000000000000,   -50,    4 },

            { 0xE8D4A51000000000,   -24,   12 },

            { 0xAD78EBC5AC620000,     3,   20 },

            { 0x813F3978F8940984,    30,   28 },

            { 0xC097CE7BC90715B3,    56,   36 },

