/// how to treat CBOR tags

enum class cbor_tag_handler_t

{

    error,  ///< throw a parse_error exception in case of a tag

    ignore   ///< ignore tags

};



/*!

@brief determine system byte order



@return true if and only if system's byte order is little endian



@note from https://stackoverflow.com/a/1001328/266378

*/

static inline bool little_endianess(int num = 1) noexcept

{

    return *reinterpret_cast<char*>(&num) == 1;

}





///////////////////

// binary reader //

///////////////////



/*!

@brief deserialization of CBOR, MessagePack, and UBJSON values

*/

template<typename BasicJsonType, typename InputAdapterType, typename SAX = json_sax_dom_parser<BasicJsonType>>

class binary_reader

{

    using number_integer_t = typename BasicJsonType::number_integer_t;

    using number_unsigned_t = typename BasicJsonType::number_unsigned_t;

    using number_float_t = typename BasicJsonType::number_float_t;

    using string_t = typename BasicJsonType::string_t;

    using binary_t = typename BasicJsonType::binary_t;

    using json_sax_t = SAX;

    using char_type = typename InputAdapterType::char_type;

    using char_int_type = typename std::char_traits<char_type>::int_type;



  public:

    /*!

    @brief create a binary reader



    @param[in] adapter  input adapter to read from

    */

    explicit binary_reader(InputAdapterType&& adapter) noexcept : ia(std::move(adapter))

    {

        (void)detail::is_sax_static_asserts<SAX, BasicJsonType> {};

    }



    // make class move-only

    binary_reader(const binary_reader&) = delete;

    binary_reader(binary_reader&&) = default; // NOLINT(hicpp-noexcept-move,performance-noexcept-move-constructor)

    binary_reader& operator=(const binary_reader&) = delete;

    binary_reader& operator=(binary_reader&&) = default; // NOLINT(hicpp-noexcept-move,performance-noexcept-move-constructor)

    ~binary_reader() = default;



    /*!

    @param[in] format  the binary format to parse

    @param[in] sax_    a SAX event processor

    @param[in] strict  whether to expect the input to be consumed completed

    @param[in] tag_handler  how to treat CBOR tags



    @return whether parsing was successful

    */

    JSON_HEDLEY_NON_NULL(3)

    bool sax_parse(const input_format_t format,

                   json_sax_t* sax_,

                   const bool strict = true,

                   const cbor_tag_handler_t tag_handler = cbor_tag_handler_t::error)

    {

        sax = sax_;

        bool result = false;



        switch (format)

        {

            case input_format_t::bson:

                result = parse_bson_internal();

                break;



            case input_format_t::cbor:

                result = parse_cbor_internal(true, tag_handler);

                break;



            case input_format_t::msgpack:

                result = parse_msgpack_internal();

                break;



            case input_format_t::ubjson:

                result = parse_ubjson_internal();

                break;



            default:            // LCOV_EXCL_LINE

                JSON_ASSERT(false); // NOLINT(cert-dcl03-c,hicpp-static-assert,misc-static-assert) LCOV_EXCL_LINE

        }



        // strict mode: next byte must be EOF

        if (result && strict)

        {

            if (format == input_format_t::ubjson)

            {

                get_ignore_noop();

            }

            else

            {

                get();

            }



            if (JSON_HEDLEY_UNLIKELY(current != std::char_traits<char_type>::eof()))

            {

                return sax->parse_error(chars_read, get_token_string(),

                                        parse_error::create(110, chars_read, exception_message(format, "expected end of input; last byte: 0x" + get_token_string(), "value"), BasicJsonType()));

            }

        }



        return result;

    }



  private:

    //////////

    // BSON //

    //////////



    /*!

    @brief Reads in a BSON-object and passes it to the SAX-parser.

    @return whether a valid BSON-value was passed to the SAX parser

    */

    bool parse_bson_internal()

    {

        std::int32_t document_size{};

        get_number<std::int32_t, true>(input_format_t::bson, document_size);



        if (JSON_HEDLEY_UNLIKELY(!sax->start_object(std::size_t(-1))))

        {

            return false;

        }



        if (JSON_HEDLEY_UNLIKELY(!parse_bson_element_list(/*is_array*/false)))

        {

            return false;

        }



        return sax->end_object();

    }



    /*!

    @brief Parses a C-style string from the BSON input.

    @param[in,out] result  A reference to the string variable where the read

                            string is to be stored.

    @return `true` if the \x00-byte indicating the end of the string was

             encountered before the EOF; false` indicates an unexpected EOF.

    */

    bool get_bson_cstr(string_t& result)

    {

        auto out = std::back_inserter(result);

        while (true)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::bson, "cstring")))

            {

                return false;

            }

            if (current == 0x00)

            {

                return true;

            }

            *out++ = static_cast<typename string_t::value_type>(current);

        }

    }



    /*!

    @brief Parses a zero-terminated string of length @a len from the BSON

           input.

    @param[in] len  The length (including the zero-byte at the end) of the

                    string to be read.

    @param[in,out] result  A reference to the string variable where the read

                            string is to be stored.

    @tparam NumberType The type of the length @a len

    @pre len >= 1

    @return `true` if the string was successfully parsed

    */

    template<typename NumberType>

    bool get_bson_string(const NumberType len, string_t& result)

    {

        if (JSON_HEDLEY_UNLIKELY(len < 1))

        {

            auto last_token = get_token_string();

            return sax->parse_error(chars_read, last_token, parse_error::create(112, chars_read, exception_message(input_format_t::bson, "string length must be at least 1, is " + std::to_string(len), "string"), BasicJsonType()));

        }



        return get_string(input_format_t::bson, len - static_cast<NumberType>(1), result) && get() != std::char_traits<char_type>::eof();

    }



    /*!

    @brief Parses a byte array input of length @a len from the BSON input.

    @param[in] len  The length of the byte array to be read.

    @param[in,out] result  A reference to the binary variable where the read

                            array is to be stored.

    @tparam NumberType The type of the length @a len

    @pre len >= 0

    @return `true` if the byte array was successfully parsed

    */

    template<typename NumberType>

    bool get_bson_binary(const NumberType len, binary_t& result)

    {

        if (JSON_HEDLEY_UNLIKELY(len < 0))

        {

            auto last_token = get_token_string();

            return sax->parse_error(chars_read, last_token, parse_error::create(112, chars_read, exception_message(input_format_t::bson, "byte array length cannot be negative, is " + std::to_string(len), "binary"), BasicJsonType()));

        }



        // All BSON binary values have a subtype

        std::uint8_t subtype{};

        get_number<std::uint8_t>(input_format_t::bson, subtype);

        result.set_subtype(subtype);



        return get_binary(input_format_t::bson, len, result);

    }



    /*!

    @brief Read a BSON document element of the given @a element_type.

    @param[in] element_type The BSON element type, c.f. http://bsonspec.org/spec.html

    @param[in] element_type_parse_position The position in the input stream,

               where the `element_type` was read.

    @warning Not all BSON element types are supported yet. An unsupported

             @a element_type will give rise to a parse_error.114:

             Unsupported BSON record type 0x...

    @return whether a valid BSON-object/array was passed to the SAX parser

    */

    bool parse_bson_element_internal(const char_int_type element_type,

                                     const std::size_t element_type_parse_position)

    {

        switch (element_type)

        {

            case 0x01: // double

            {

                double number{};

                return get_number<double, true>(input_format_t::bson, number) && sax->number_float(static_cast<number_float_t>(number), "");

            }



            case 0x02: // string

            {

                std::int32_t len{};

                string_t value;

                return get_number<std::int32_t, true>(input_format_t::bson, len) && get_bson_string(len, value) && sax->string(value);

            }



            case 0x03: // object

            {

                return parse_bson_internal();

            }



            case 0x04: // array

            {

                return parse_bson_array();

            }



            case 0x05: // binary

            {

                std::int32_t len{};

                binary_t value;

                return get_number<std::int32_t, true>(input_format_t::bson, len) && get_bson_binary(len, value) && sax->binary(value);

            }



            case 0x08: // boolean

            {

                return sax->boolean(get() != 0);

            }



            case 0x0A: // null

            {

                return sax->null();

            }



            case 0x10: // int32

            {

                std::int32_t value{};

                return get_number<std::int32_t, true>(input_format_t::bson, value) && sax->number_integer(value);

            }



            case 0x12: // int64

            {

                std::int64_t value{};

                return get_number<std::int64_t, true>(input_format_t::bson, value) && sax->number_integer(value);

            }



            default: // anything else not supported (yet)

            {

                std::array<char, 3> cr{{}};

                (std::snprintf)(cr.data(), cr.size(), "%.2hhX", static_cast<unsigned char>(element_type)); // NOLINT(cppcoreguidelines-pro-type-vararg,hicpp-vararg)

                return sax->parse_error(element_type_parse_position, std::string(cr.data()), parse_error::create(114, element_type_parse_position, "Unsupported BSON record type 0x" + std::string(cr.data()), BasicJsonType()));

            }

        }

    }



    /*!

    @brief Read a BSON element list (as specified in the BSON-spec)



    The same binary layout is used for objects and arrays, hence it must be

    indicated with the argument @a is_array which one is expected

    (true --> array, false --> object).



    @param[in] is_array Determines if the element list being read is to be

                        treated as an object (@a is_array == false), or as an

                        array (@a is_array == true).

    @return whether a valid BSON-object/array was passed to the SAX parser

    */

    bool parse_bson_element_list(const bool is_array)

    {

        string_t key;



        while (auto element_type = get())

        {

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::bson, "element list")))

            {

                return false;

            }



            const std::size_t element_type_parse_position = chars_read;

            if (JSON_HEDLEY_UNLIKELY(!get_bson_cstr(key)))

            {

                return false;

            }



            if (!is_array && !sax->key(key))

            {

                return false;

            }



            if (JSON_HEDLEY_UNLIKELY(!parse_bson_element_internal(element_type, element_type_parse_position)))

            {

                return false;

            }



            // get_bson_cstr only appends

            key.clear();

        }



        return true;

    }



    /*!

    @brief Reads an array from the BSON input and passes it to the SAX-parser.

    @return whether a valid BSON-array was passed to the SAX parser

    */

    bool parse_bson_array()

    {

        std::int32_t document_size{};

        get_number<std::int32_t, true>(input_format_t::bson, document_size);



        if (JSON_HEDLEY_UNLIKELY(!sax->start_array(std::size_t(-1))))

        {

            return false;

        }



        if (JSON_HEDLEY_UNLIKELY(!parse_bson_element_list(/*is_array*/true)))

        {

            return false;

        }



        return sax->end_array();

    }



    //////////

    // CBOR //

    //////////



    /*!

    @param[in] get_char  whether a new character should be retrieved from the

                         input (true) or whether the last read character should

                         be considered instead (false)

    @param[in] tag_handler how CBOR tags should be treated



    @return whether a valid CBOR value was passed to the SAX parser

    */

    bool parse_cbor_internal(const bool get_char,

                             const cbor_tag_handler_t tag_handler)

    {

        switch (get_char ? get() : current)

        {

            // EOF

            case std::char_traits<char_type>::eof():

                return unexpect_eof(input_format_t::cbor, "value");



            // Integer 0x00..0x17 (0..23)

            case 0x00:

            case 0x01:

            case 0x02:

            case 0x03:

            case 0x04:

            case 0x05:

            case 0x06:

            case 0x07:

            case 0x08:

            case 0x09:

            case 0x0A:

            case 0x0B:

            case 0x0C:

            case 0x0D:

            case 0x0E:

            case 0x0F:

            case 0x10:

            case 0x11:

            case 0x12:

            case 0x13:

            case 0x14:

            case 0x15:

            case 0x16:

            case 0x17:

                return sax->number_unsigned(static_cast<number_unsigned_t>(current));



            case 0x18: // Unsigned integer (one-byte uint8_t follows)

            {

                std::uint8_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_unsigned(number);

            }



            case 0x19: // Unsigned integer (two-byte uint16_t follows)

            {

                std::uint16_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_unsigned(number);

            }



            case 0x1A: // Unsigned integer (four-byte uint32_t follows)

            {

                std::uint32_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_unsigned(number);

            }



            case 0x1B: // Unsigned integer (eight-byte uint64_t follows)

            {

                std::uint64_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_unsigned(number);

            }



            // Negative integer -1-0x00..-1-0x17 (-1..-24)

            case 0x20:

            case 0x21:

            case 0x22:

            case 0x23:

            case 0x24:

            case 0x25:

            case 0x26:

            case 0x27:

            case 0x28:

            case 0x29:

            case 0x2A:

            case 0x2B:

            case 0x2C:

            case 0x2D:

            case 0x2E:

            case 0x2F:

            case 0x30:

            case 0x31:

            case 0x32:

            case 0x33:

            case 0x34:

            case 0x35:

            case 0x36:

            case 0x37:

                return sax->number_integer(static_cast<std::int8_t>(0x20 - 1 - current));



            case 0x38: // Negative integer (one-byte uint8_t follows)

            {

                std::uint8_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_integer(static_cast<number_integer_t>(-1) - number);

            }



            case 0x39: // Negative integer -1-n (two-byte uint16_t follows)

            {

                std::uint16_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_integer(static_cast<number_integer_t>(-1) - number);

            }



            case 0x3A: // Negative integer -1-n (four-byte uint32_t follows)

            {

                std::uint32_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_integer(static_cast<number_integer_t>(-1) - number);

            }



            case 0x3B: // Negative integer -1-n (eight-byte uint64_t follows)

            {

                std::uint64_t number{};

                return get_number(input_format_t::cbor, number) && sax->number_integer(static_cast<number_integer_t>(-1)

                        - static_cast<number_integer_t>(number));

            }



            // Binary data (0x00..0x17 bytes follow)

            case 0x40:

            case 0x41:

            case 0x42:

            case 0x43:

            case 0x44:

            case 0x45:

            case 0x46:

            case 0x47:

            case 0x48:

            case 0x49:

            case 0x4A:

            case 0x4B:

            case 0x4C:

            case 0x4D:

            case 0x4E:

            case 0x4F:

            case 0x50:

            case 0x51:

            case 0x52:

            case 0x53:

            case 0x54:

            case 0x55:

            case 0x56:

            case 0x57:

            case 0x58: // Binary data (one-byte uint8_t for n follows)

            case 0x59: // Binary data (two-byte uint16_t for n follow)

            case 0x5A: // Binary data (four-byte uint32_t for n follow)

            case 0x5B: // Binary data (eight-byte uint64_t for n follow)

            case 0x5F: // Binary data (indefinite length)

            {

                binary_t b;

                return get_cbor_binary(b) && sax->binary(b);

            }



            // UTF-8 string (0x00..0x17 bytes follow)

            case 0x60:

            case 0x61:

            case 0x62:

            case 0x63:

            case 0x64:

            case 0x65:

            case 0x66:

