            case 0xCD: // uint 16

            {

                std::uint16_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_unsigned(number);

            }



            case 0xCE: // uint 32

            {

                std::uint32_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_unsigned(number);

            }



            case 0xCF: // uint 64

            {

                std::uint64_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_unsigned(number);

            }



            case 0xD0: // int 8

            {

                std::int8_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_integer(number);

            }



            case 0xD1: // int 16

            {

                std::int16_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_integer(number);

            }



            case 0xD2: // int 32

            {

                std::int32_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_integer(number);

            }



            case 0xD3: // int 64

            {

                std::int64_t number{};

                return get_number(input_format_t::msgpack, number) && sax->number_integer(number);

            }



            case 0xDC: // array 16

            {

                std::uint16_t len{};

                return get_number(input_format_t::msgpack, len) && get_msgpack_array(static_cast<std::size_t>(len));

            }



            case 0xDD: // array 32

            {

                std::uint32_t len{};

                return get_number(input_format_t::msgpack, len) && get_msgpack_array(static_cast<std::size_t>(len));

            }



            case 0xDE: // map 16

            {

                std::uint16_t len{};

                return get_number(input_format_t::msgpack, len) && get_msgpack_object(static_cast<std::size_t>(len));

            }



            case 0xDF: // map 32

            {

                std::uint32_t len{};

                return get_number(input_format_t::msgpack, len) && get_msgpack_object(static_cast<std::size_t>(len));

            }



            // negative fixint

            case 0xE0:

            case 0xE1:

            case 0xE2:

            case 0xE3:

            case 0xE4:

            case 0xE5:

            case 0xE6:

            case 0xE7:

            case 0xE8:

            case 0xE9:

            case 0xEA:

            case 0xEB:

            case 0xEC:

            case 0xED:

            case 0xEE:

            case 0xEF:

            case 0xF0:

            case 0xF1:

            case 0xF2:

            case 0xF3:

            case 0xF4:

            case 0xF5:

            case 0xF6:

            case 0xF7:

            case 0xF8:

            case 0xF9:

            case 0xFA:

            case 0xFB:

            case 0xFC:

            case 0xFD:

            case 0xFE:

            case 0xFF:

                return sax->number_integer(static_cast<std::int8_t>(current));



            default: // anything else

            {

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(112, chars_read, exception_message(input_format_t::msgpack, "invalid byte: 0x" + last_token, "value"), BasicJsonType()));

            }

        }

    }



    /*!

    @brief reads a MessagePack string



    This function first reads starting bytes to determine the expected

    string length and then copies this number of bytes into a string.



    @param[out] result  created string



    @return whether string creation completed

    */

    bool get_msgpack_string(string_t& result)

    {

        if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::msgpack, "string")))

        {

            return false;

        }



        switch (current)

        {

            // fixstr

            case 0xA0:

            case 0xA1:

            case 0xA2:

            case 0xA3:

            case 0xA4:

            case 0xA5:

            case 0xA6:

            case 0xA7:

            case 0xA8:

            case 0xA9:

            case 0xAA:

            case 0xAB:

            case 0xAC:

            case 0xAD:

            case 0xAE:

            case 0xAF:

            case 0xB0:

            case 0xB1:

            case 0xB2:

            case 0xB3:

            case 0xB4:

            case 0xB5:

            case 0xB6:

            case 0xB7:

            case 0xB8:

            case 0xB9:

            case 0xBA:

            case 0xBB:

            case 0xBC:

            case 0xBD:

            case 0xBE:

            case 0xBF:

            {

                return get_string(input_format_t::msgpack, static_cast<unsigned int>(current) & 0x1Fu, result);

            }



            case 0xD9: // str 8

            {

                std::uint8_t len{};

                return get_number(input_format_t::msgpack, len) && get_string(input_format_t::msgpack, len, result);

            }



            case 0xDA: // str 16

            {

                std::uint16_t len{};

                return get_number(input_format_t::msgpack, len) && get_string(input_format_t::msgpack, len, result);

            }



            case 0xDB: // str 32

            {

                std::uint32_t len{};

                return get_number(input_format_t::msgpack, len) && get_string(input_format_t::msgpack, len, result);

            }



            default:

            {

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(113, chars_read, exception_message(input_format_t::msgpack, "expected length specification (0xA0-0xBF, 0xD9-0xDB); last byte: 0x" + last_token, "string"), BasicJsonType()));

            }

        }

    }



    /*!

    @brief reads a MessagePack byte array



    This function first reads starting bytes to determine the expected

    byte array length and then copies this number of bytes into a byte array.



    @param[out] result  created byte array



    @return whether byte array creation completed

    */

    bool get_msgpack_binary(binary_t& result)

    {

        // helper function to set the subtype

        auto assign_and_return_true = [&result](std::int8_t subtype)

        {

            result.set_subtype(static_cast<std::uint8_t>(subtype));

            return true;

        };



        switch (current)

        {

            case 0xC4: // bin 8

            {

                std::uint8_t len{};

                return get_number(input_format_t::msgpack, len) &&

                       get_binary(input_format_t::msgpack, len, result);

            }



            case 0xC5: // bin 16

            {

                std::uint16_t len{};

                return get_number(input_format_t::msgpack, len) &&

                       get_binary(input_format_t::msgpack, len, result);

            }



            case 0xC6: // bin 32

            {

                std::uint32_t len{};

                return get_number(input_format_t::msgpack, len) &&

                       get_binary(input_format_t::msgpack, len, result);

            }



            case 0xC7: // ext 8

            {

                std::uint8_t len{};

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, len) &&

                       get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, len, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xC8: // ext 16

            {

                std::uint16_t len{};

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, len) &&

                       get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, len, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xC9: // ext 32

            {

                std::uint32_t len{};

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, len) &&

                       get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, len, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xD4: // fixext 1

            {

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, 1, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xD5: // fixext 2

            {

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, 2, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xD6: // fixext 4

            {

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, 4, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xD7: // fixext 8

            {

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, 8, result) &&

                       assign_and_return_true(subtype);

            }



            case 0xD8: // fixext 16

            {

                std::int8_t subtype{};

                return get_number(input_format_t::msgpack, subtype) &&

                       get_binary(input_format_t::msgpack, 16, result) &&

                       assign_and_return_true(subtype);

            }



            default:           // LCOV_EXCL_LINE

                return false;  // LCOV_EXCL_LINE

        }

    }



    /*!

    @param[in] len  the length of the array

    @return whether array creation completed

    */

    bool get_msgpack_array(const std::size_t len)

    {

        if (JSON_HEDLEY_UNLIKELY(!sax->start_array(len)))

        {

            return false;

        }



        for (std::size_t i = 0; i < len; ++i)

        {

            if (JSON_HEDLEY_UNLIKELY(!parse_msgpack_internal()))

            {

                return false;

            }

        }



        return sax->end_array();

    }



    /*!

    @param[in] len  the length of the object

    @return whether object creation completed

    */

    bool get_msgpack_object(const std::size_t len)

    {

        if (JSON_HEDLEY_UNLIKELY(!sax->start_object(len)))

        {

            return false;

        }



        string_t key;

        for (std::size_t i = 0; i < len; ++i)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!get_msgpack_string(key) || !sax->key(key)))

            {

                return false;

            }



            if (JSON_HEDLEY_UNLIKELY(!parse_msgpack_internal()))

            {

                return false;

            }

            key.clear();

        }



        return sax->end_object();

    }



    ////////////

    // UBJSON //

    ////////////



    /*!

    @param[in] get_char  whether a new character should be retrieved from the

                         input (true, default) or whether the last read

                         character should be considered instead



    @return whether a valid UBJSON value was passed to the SAX parser

    */

    bool parse_ubjson_internal(const bool get_char = true)

    {

        return get_ubjson_value(get_char ? get_ignore_noop() : current);

    }



    /*!

    @brief reads a UBJSON string



    This function is either called after reading the 'S' byte explicitly

    indicating a string, or in case of an object key where the 'S' byte can be

    left out.



    @param[out] result   created string

    @param[in] get_char  whether a new character should be retrieved from the

                         input (true, default) or whether the last read

                         character should be considered instead



    @return whether string creation completed

    */

    bool get_ubjson_string(string_t& result, const bool get_char = true)

    {

        if (get_char)

        {

            get();  // TODO(niels): may we ignore N here?

        }



        if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::ubjson, "value")))

        {

            return false;

        }



        switch (current)

        {

            case 'U':

            {

                std::uint8_t len{};

                return get_number(input_format_t::ubjson, len) && get_string(input_format_t::ubjson, len, result);

            }



            case 'i':

            {

                std::int8_t len{};

                return get_number(input_format_t::ubjson, len) && get_string(input_format_t::ubjson, len, result);

            }



            case 'I':

            {

                std::int16_t len{};

                return get_number(input_format_t::ubjson, len) && get_string(input_format_t::ubjson, len, result);

            }



            case 'l':

            {

                std::int32_t len{};

                return get_number(input_format_t::ubjson, len) && get_string(input_format_t::ubjson, len, result);

            }



            case 'L':

            {

                std::int64_t len{};

                return get_number(input_format_t::ubjson, len) && get_string(input_format_t::ubjson, len, result);

            }



            default:

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(113, chars_read, exception_message(input_format_t::ubjson, "expected length type specification (U, i, I, l, L); last byte: 0x" + last_token, "string"), BasicJsonType()));

        }

    }



    /*!

    @param[out] result  determined size

    @return whether size determination completed

    */

    bool get_ubjson_size_value(std::size_t& result)

    {

        switch (get_ignore_noop())

        {

            case 'U':

            {

                std::uint8_t number{};

                if (JSON_HEDLEY_UNLIKELY(!get_number(input_format_t::ubjson, number)))

                {

                    return false;

                }

                result = static_cast<std::size_t>(number);

                return true;

            }



            case 'i':

            {

                std::int8_t number{};

                if (JSON_HEDLEY_UNLIKELY(!get_number(input_format_t::ubjson, number)))

                {

                    return false;

                }

                result = static_cast<std::size_t>(number); // NOLINT(bugprone-signed-char-misuse,cert-str34-c): number is not a char

                return true;

            }



            case 'I':

            {

                std::int16_t number{};

                if (JSON_HEDLEY_UNLIKELY(!get_number(input_format_t::ubjson, number)))

                {

                    return false;

                }

                result = static_cast<std::size_t>(number);

                return true;

            }



            case 'l':

            {

                std::int32_t number{};

                if (JSON_HEDLEY_UNLIKELY(!get_number(input_format_t::ubjson, number)))

                {

                    return false;

                }

                result = static_cast<std::size_t>(number);

                return true;

            }



            case 'L':

            {

                std::int64_t number{};

                if (JSON_HEDLEY_UNLIKELY(!get_number(input_format_t::ubjson, number)))

                {

                    return false;

                }

                result = static_cast<std::size_t>(number);

                return true;

            }



            default:

            {

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(113, chars_read, exception_message(input_format_t::ubjson, "expected length type specification (U, i, I, l, L) after '#'; last byte: 0x" + last_token, "size"), BasicJsonType()));

            }

        }

    }



    /*!

    @brief determine the type and size for a container



    In the optimized UBJSON format, a type and a size can be provided to allow

    for a more compact representation.



    @param[out] result  pair of the size and the type



    @return whether pair creation completed

    */

    bool get_ubjson_size_type(std::pair<std::size_t, char_int_type>& result)

    {

        result.first = string_t::npos; // size

        result.second = 0; // type



        get_ignore_noop();



        if (current == '$')

        {

            result.second = get();  // must not ignore 'N', because 'N' maybe the type

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::ubjson, "type")))

            {

                return false;

            }



            get_ignore_noop();

            if (JSON_HEDLEY_UNLIKELY(current != '#'))

            {

                if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::ubjson, "value")))

                {

                    return false;

                }

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(112, chars_read, exception_message(input_format_t::ubjson, "expected '#' after type information; last byte: 0x" + last_token, "size"), BasicJsonType()));

            }



            return get_ubjson_size_value(result.first);

        }



        if (current == '#')

        {

            return get_ubjson_size_value(result.first);

        }



        return true;

    }



    /*!

    @param prefix  the previously read or set type prefix

    @return whether value creation completed

    */

    bool get_ubjson_value(const char_int_type prefix)

    {

        switch (prefix)

        {

            case std::char_traits<char_type>::eof():  // EOF

                return unexpect_eof(input_format_t::ubjson, "value");



            case 'T':  // true

                return sax->boolean(true);

            case 'F':  // false

                return sax->boolean(false);



            case 'Z':  // null

                return sax->null();



            case 'U':

            {

                std::uint8_t number{};

                return get_number(input_format_t::ubjson, number) && sax->number_unsigned(number);

            }



            case 'i':

            {

                std::int8_t number{};

                return get_number(input_format_t::ubjson, number) && sax->number_integer(number);

            }



            case 'I':

            {

                std::int16_t number{};

                return get_number(input_format_t::ubjson, number) && sax->number_integer(number);

            }



            case 'l':

            {

                std::int32_t number{};

                return get_number(input_format_t::ubjson, number) && sax->number_integer(number);

            }



            case 'L':

            {

                std::int64_t number{};

                return get_number(input_format_t::ubjson, number) && sax->number_integer(number);

            }



            case 'd':

            {

                float number{};

                return get_number(input_format_t::ubjson, number) && sax->number_float(static_cast<number_float_t>(number), "");

            }



            case 'D':

            {

                double number{};

                return get_number(input_format_t::ubjson, number) && sax->number_float(static_cast<number_float_t>(number), "");

            }



            case 'H':

            {

                return get_ubjson_high_precision_number();

            }



            case 'C':  // char

            {

                get();

                if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::ubjson, "char")))

                {

                    return false;

                }

                if (JSON_HEDLEY_UNLIKELY(current > 127))

                {

                    auto last_token = get_token_string();

                    return sax->parse_error(chars_read, last_token, parse_error::create(113, chars_read, exception_message(input_format_t::ubjson, "byte after 'C' must be in range 0x00..0x7F; last byte: 0x" + last_token, "char"), BasicJsonType()));

                }

                string_t s(1, static_cast<typename string_t::value_type>(current));

                return sax->string(s);

            }



            case 'S':  // string

            {

                string_t s;

                return get_ubjson_string(s) && sax->string(s);

            }



            case '[':  // array

                return get_ubjson_array();



            case '{':  // object

                return get_ubjson_object();



            default: // anything else

            {

                auto last_token = get_token_string();

                return sax->parse_error(chars_read, last_token, parse_error::create(112, chars_read, exception_message(input_format_t::ubjson, "invalid byte: 0x" + last_token, "value"), BasicJsonType()));

            }

        }

    }



    /*!

    @return whether array creation completed

    */

    bool get_ubjson_array()

    {

        std::pair<std::size_t, char_int_type> size_and_type;

        if (JSON_HEDLEY_UNLIKELY(!get_ubjson_size_type(size_and_type)))

        {

            return false;

        }



        if (size_and_type.first != string_t::npos)

        {

            if (JSON_HEDLEY_UNLIKELY(!sax->start_array(size_and_type.first)))

            {

                return false;

            }



            if (size_and_type.second != 0)

            {

                if (size_and_type.second != 'N')

                {

                    for (std::size_t i = 0; i < size_and_type.first; ++i)

                    {

                        if (JSON_HEDLEY_UNLIKELY(!get_ubjson_value(size_and_type.second)))

                        {

                            return false;

                        }

                    }

                }

            }

            else

            {

                for (std::size_t i = 0; i < size_and_type.first; ++i)

                {

                    if (JSON_HEDLEY_UNLIKELY(!parse_ubjson_internal()))

                    {

                        return false;

                    }

                }

            }

        }

        else

        {

            if (JSON_HEDLEY_UNLIKELY(!sax->start_array(std::size_t(-1))))

            {

                return false;

            }



            while (current != ']')

            {

                if (JSON_HEDLEY_UNLIKELY(!parse_ubjson_internal(false)))

                {

                    return false;

                }

                get_ignore_noop();

            }

        }



        return sax->end_array();

    }



    /*!

    @return whether object creation completed

    */

    bool get_ubjson_object()

    {

        std::pair<std::size_t, char_int_type> size_and_type;

        if (JSON_HEDLEY_UNLIKELY(!get_ubjson_size_type(size_and_type)))

        {

            return false;

        }



        string_t key;

        if (size_and_type.first != string_t::npos)

        {

            if (JSON_HEDLEY_UNLIKELY(!sax->start_object(size_and_type.first)))

            {

                return false;

            }



            if (size_and_type.second != 0)

            {

                for (std::size_t i = 0; i < size_and_type.first; ++i)

                {

                    if (JSON_HEDLEY_UNLIKELY(!get_ubjson_string(key) || !sax->key(key)))

                    {

                        return false;

                    }

                    if (JSON_HEDLEY_UNLIKELY(!get_ubjson_value(size_and_type.second)))

                    {

                        return false;

                    }

                    key.clear();

                }

            }

            else

            {

                for (std::size_t i = 0; i < size_and_type.first; ++i)

                {

                    if (JSON_HEDLEY_UNLIKELY(!get_ubjson_string(key) || !sax->key(key)))

                    {

                        return false;

                    }

                    if (JSON_HEDLEY_UNLIKELY(!parse_ubjson_internal()))

                    {

                        return false;

                    }

                    key.clear();

                }

            }

        }

        else

        {

            if (JSON_HEDLEY_UNLIKELY(!sax->start_object(std::size_t(-1))))

            {

                return false;

            }



            while (current != '}')

            {

                if (JSON_HEDLEY_UNLIKELY(!get_ubjson_string(key, false) || !sax->key(key)))

                {

                    return false;

                }

                if (JSON_HEDLEY_UNLIKELY(!parse_ubjson_internal()))

                {

                    return false;

                }

                get_ignore_noop();

                key.clear();

            }

        }



        return sax->end_object();

    }



    // Note, no reader for UBJSON binary types is implemented because they do

    // not exist



    bool get_ubjson_high_precision_number()

    {

        // get size of following number string

        std::size_t size{};

        auto res = get_ubjson_size_value(size);

        if (JSON_HEDLEY_UNLIKELY(!res))

        {

            return res;

        }



        // get number string

        std::vector<char> number_vector;

        for (std::size_t i = 0; i < size; ++i)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(input_format_t::ubjson, "number")))

            {

                return false;

            }

            number_vector.push_back(static_cast<char>(current));

        }



        // parse number string

        using ia_type = decltype(detail::input_adapter(number_vector));

        auto number_lexer = detail::lexer<BasicJsonType, ia_type>(detail::input_adapter(number_vector), false);

        const auto result_number = number_lexer.scan();

        const auto number_string = number_lexer.get_token_string();

        const auto result_remainder = number_lexer.scan();



        using token_type = typename detail::lexer_base<BasicJsonType>::token_type;



        if (JSON_HEDLEY_UNLIKELY(result_remainder != token_type::end_of_input))

        {

            return sax->parse_error(chars_read, number_string, parse_error::create(115, chars_read, exception_message(input_format_t::ubjson, "invalid number text: " + number_lexer.get_token_string(), "high-precision number"), BasicJsonType()));

        }



        switch (result_number)

        {

            case token_type::value_integer:

                return sax->number_integer(number_lexer.get_number_integer());

            case token_type::value_unsigned:

                return sax->number_unsigned(number_lexer.get_number_unsigned());

            case token_type::value_float:

                return sax->number_float(number_lexer.get_number_float(), std::move(number_string));

            default:

                return sax->parse_error(chars_read, number_string, parse_error::create(115, chars_read, exception_message(input_format_t::ubjson, "invalid number text: " + number_lexer.get_token_string(), "high-precision number"), BasicJsonType()));

        }

    }



    ///////////////////////

    // Utility functions //

    ///////////////////////



    /*!

    @brief get next character from the input



    This function provides the interface to the used input adapter. It does

    not throw in case the input reached EOF, but returns a -'ve valued

    `std::char_traits<char_type>::eof()` in that case.



    @return character read from the input

    */

    char_int_type get()

    {

        ++chars_read;

        return current = ia.get_character();

    }



    /*!

    @return character read from the input after ignoring all 'N' entries

    */

    char_int_type get_ignore_noop()

    {

        do

        {

            get();

        }

        while (current == 'N');



        return current;

    }



    /*

    @brief read a number from the input



    @tparam NumberType the type of the number

    @param[in] format   the current format (for diagnostics)

    @param[out] result  number of type @a NumberType



    @return whether conversion completed



    @note This function needs to respect the system's endianess, because

          bytes in CBOR, MessagePack, and UBJSON are stored in network order

          (big endian) and therefore need reordering on little endian systems.

    */

    template<typename NumberType, bool InputIsLittleEndian = false>

    bool get_number(const input_format_t format, NumberType& result)

    {

        // step 1: read input into array with system's byte order

        std::array<std::uint8_t, sizeof(NumberType)> vec{};

        for (std::size_t i = 0; i < sizeof(NumberType); ++i)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(format, "number")))

            {

                return false;

            }



            // reverse byte order prior to conversion if necessary

            if (is_little_endian != InputIsLittleEndian)

            {

                vec[sizeof(NumberType) - i - 1] = static_cast<std::uint8_t>(current);

            }

            else

            {

                vec[i] = static_cast<std::uint8_t>(current); // LCOV_EXCL_LINE

            }

        }



        // step 2: convert array into number of type T and return

        std::memcpy(&result, vec.data(), sizeof(NumberType));

        return true;

    }



    /*!

    @brief create a string by reading characters from the input



    @tparam NumberType the type of the number

    @param[in] format the current format (for diagnostics)

    @param[in] len number of characters to read

    @param[out] result string created by reading @a len bytes



    @return whether string creation completed



    @note We can not reserve @a len bytes for the result, because @a len

          may be too large. Usually, @ref unexpect_eof() detects the end of

          the input before we run out of string memory.

    */

    template<typename NumberType>

    bool get_string(const input_format_t format,

                    const NumberType len,

                    string_t& result)

    {

        bool success = true;

        for (NumberType i = 0; i < len; i++)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(format, "string")))

            {

                success = false;

                break;

            }

            result.push_back(static_cast<typename string_t::value_type>(current));

        }

        return success;

    }



    /*!

    @brief create a byte array by reading bytes from the input



    @tparam NumberType the type of the number

    @param[in] format the current format (for diagnostics)

    @param[in] len number of bytes to read

    @param[out] result byte array created by reading @a len bytes



    @return whether byte array creation completed



    @note We can not reserve @a len bytes for the result, because @a len

          may be too large. Usually, @ref unexpect_eof() detects the end of

          the input before we run out of memory.

    */

    template<typename NumberType>

    bool get_binary(const input_format_t format,

                    const NumberType len,

                    binary_t& result)

    {

        bool success = true;

        for (NumberType i = 0; i < len; i++)

        {

            get();

            if (JSON_HEDLEY_UNLIKELY(!unexpect_eof(format, "binary")))

            {

                success = false;

                break;

            }

            result.push_back(static_cast<std::uint8_t>(current));

        }

        return success;

    }



    /*!

    @param[in] format   the current format (for diagnostics)

    @param[in] context  further context information (for diagnostics)

    @return whether the last read character is not EOF

    */

    JSON_HEDLEY_NON_NULL(3)

    bool unexpect_eof(const input_format_t format, const char* context) const

    {

        if (JSON_HEDLEY_UNLIKELY(current == std::char_traits<char_type>::eof()))

        {

            return sax->parse_error(chars_read, "<end of file>",

                                    parse_error::create(110, chars_read, exception_message(format, "unexpected end of input", context), BasicJsonType()));

        }

        return true;

    }



    /*!

    @return a string representation of the last read byte

    */

    std::string get_token_string() const

    {

        std::array<char, 3> cr{{}};

        (std::snprintf)(cr.data(), cr.size(), "%.2hhX", static_cast<unsigned char>(current)); // NOLINT(cppcoreguidelines-pro-type-vararg,hicpp-vararg)

        return std::string{cr.data()};

    }



    /*!

    @param[in] format   the current format

    @param[in] detail   a detailed error message

    @param[in] context  further context information

    @return a message string to use in the parse_error exceptions

    */

    std::string exception_message(const input_format_t format,

                                  const std::string& detail,

                                  const std::string& context) const

    {

        std::string error_msg = "syntax error while parsing ";



        switch (format)

        {

            case input_format_t::cbor:

                error_msg += "CBOR";

                break;



            case input_format_t::msgpack:

                error_msg += "MessagePack";

                break;



            case input_format_t::ubjson:

                error_msg += "UBJSON";

                break;



            case input_format_t::bson:

                error_msg += "BSON";

                break;



            default:            // LCOV_EXCL_LINE

                JSON_ASSERT(false); // NOLINT(cert-dcl03-c,hicpp-static-assert,misc-static-assert) LCOV_EXCL_LINE

        }



        return error_msg + " " + context + ": " + detail;

    }



  private:

    /// input adapter

    InputAdapterType ia;



    /// the current character

    char_int_type current = std::char_traits<char_type>::eof();



    /// the number of characters read

    std::size_t chars_read = 0;



    /// whether we can assume little endianess

    const bool is_little_endian = little_endianess();



    /// the SAX parser

    json_sax_t* sax = nullptr;

};

}  // namespace detail

}  // namespace nlohmann
