{

    // 定义新的枚举变量

    Color::Type c = Color::RED;

    cout<<c<<endl;  

    /**

     * 上述的另一种方法：

     * using namespace Color; // 定义新的枚举变量

     * Type c = RED;

     */

    Color1 c1;

    cout<<c1.RED<<endl;



    Color1::Type c11 = Color1::BLUE;

    cout<<c11<<endl;



    Color2 c2 = Color2::RED;

    cout << static_cast<int>(c2) << endl;



    char c3 = static_cast<char>(Color3::RED);

    cout<<c3<<endl;

    return 0;

}
