    using nullptr_t = decltype(nullptr);

    nullptr_t  nu;

    int * p =NULL;

    if(p==nu)

        cout<<"NULL"<<endl;





    typedef decltype(vec.begin()) vectype;



    for(vectype i=vec.begin();i!=vec.end();i++)

        cout<<*i<<" ";

    cout<<endl;



    /**

     * 匿名结构体

     */

    struct

    {

        int d ;

        double b;

    }anon_s;



    decltype(anon_s) as; // 定义了一个上面匿名的结构体



    cout<<multiply(11,2)<<endl;



    return 0;

}
