template <class T>

//实现降序

void sort(T *a, int n){

    for (int i=0;i<n;i++){

        int p=i;

        for (int j=i;j<n;j++)

            if(a[p]<a[j])

                p=j;

        T t=a[i];	

        a[i]=a[p];		

        a[p]=t;

    }

}

template <class T>

void display(T& a,int n) {

    for(int i=0;i<n;i++)

        cout<<a[i]<<"\t"<<endl;

}





